const initialState ={
    aluno: {id:0,ra:'',nome:'',codCurso:0},
    lista:[],
    listaCurso:[]

}

export default function CrudCarometro(props){

    const[curso ,setCurso] = useState(initialState.curso);
    const[aluno , setAluno] = useState(initialState.aluno);


    useEffect(()=>{

        axios(urlAPI).then(resp =>{
            setAluno(resp.data)
           })
    },[aluno])

    useEffect(()=>{
        axios(urlAPICurso).then(resp =>{
            setCurso(resp.data)
        })
    },[curso])


    function  getListaAtualizada  (curso, add = true) {
        const   listas = lista.filter(a => a.id !== curso.id);
          if(add) listas.unshift(curso);
          return listas;
         }

       const  carregar= (curso)=>{
         setCurso(curso)

      }
}
