import React, {Component} from "react";
import axios from 'axios';
import './CrudCarometro.css';
import Main from '../template/Main';

import { useEffect, useState } from 'react';

const title ="Carometro";

const urlAPI = "http://localhost:5278/api/Aluno";
const urlAPICurso = "http://localhost:5278/api/Curso";



const initialState ={
    aluno: {id:0,ra:'',nome:'',codCurso:0},
    lista:[],
    listaCurso:[]

}

export default function CrudCarometro(props){

    const[curso ,setCurso] = useState(initialState.lis);
    const[aluno , setAluno] = useState(initialState.aluno);


    useEffect(()=>{

        axios(urlAPI).then(resp =>{
            setAluno(resp.data)
           })
    },[aluno])

    useEffect(()=>{
        axios(urlAPICurso).then(resp =>{
            setCurso(resp.data)
        })
    },[curso])


    function  getListaAtualizadaAluno  (aluno, add = true) {
        const   listas = aluno.filter(a => a.id !== curso.id);
          if(add) listas.unshift(aluno);
          return listas;
         }
       const  carregarAluno= (aluno)=>{
        setAluno(aluno)
      }
      function  getListaAtualizadaCurso  (curso, add = true) {
        const   listas = curso.filter(a => a.id !== curso.id);
          if(add) listas.unshift(curso);
          return listas;
         }
       const  carregarCurso= (curso)=>{
         setCurso(curso)
      }
     function atualizaCampo(evento){
        const aluno  = {...this.state.aluno};

        aluno[evento.target.name]= evento.target.value;

        this.setState({aluno});
       }
      const renderForm = () =>{
        return(
            <div  className="inclui-container">
               <label>Curso</label>
               < select name = "codCurso" onChange={e=>{this.atualizaCampo(e)}}>
                    {listaCurso.map((curso)=>

               <option name = "codCurso"
                   value={curso.codCurso}
               >
                {curso.nomeCurso}
                </option> 
                    )}
                 </select>


                 </div>
        )
    }
    return(
        <Main title={title}>
            {renderForm()}
            </Main>
        )
}
