const title ="Cadastro de Curso";

const urlAPI ="http://localhost:5278/api/Curso";

const initialState={
    curso:{nomeCurso:'',codCurso:0,periodo:''},
    lista:[]
}

export default function CrudCurso(){

const[setCurso] = useState();

